var waterLevel = document.getElementById("myChart");

var colors = {
    safeConditionColor : 'rgba(7, 244, 34, 0.1)',
    safeConditionColorOverlay:'rgba(7, 244, 34, 0.8)',
    dangerConditionColor : 'rgba(244, 67, 54, 0.1)',
    dangerConditionColorOverlay: 'rgba(244, 67, 54, 0.8)'
}

snd = new Audio("data:audio/wav;base64,");

function getdevice(){
    $.get("/data")
        .done(function(data){
            console.log(data)
            var today = new Date()
            var time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds()
            if(data["distance"] > data["safeDistance"]){
                document.getElementById("arrow-icon").classList.remove("safe-icon-color")
                document.getElementById("arrow-icon").classList.remove("danger-icon-color")
                document.getElementById("arrow-icon").classList.add("safe-icon-color")

                document.getElementById("alert-icon").classList.remove("safe-icon-color");
                document.getElementById("alert-icon").classList.remove("alert-icon-color");
                document.getElementById("alert-icon").classList.add("safe-icon-color");
            }else{
                document.getElementById("arrow-icon").classList.remove("safe-icon-color")
                document.getElementById("arrow-icon").classList.remove("danger-icon-color")
                document.getElementById("arrow-icon").classList.add("danger-icon-color")

                document.getElementById("alert-icon").classList.remove("safe-icon-color");
                document.getElementById("alert-icon").classList.remove("alert-icon-color");
                document.getElementById("alert-icon").classList.add("alert-icon-color");
            }

            addData(waterLevel_chart, time, data["distance"],
                data["distance"] > data["safeDistance"] ? colors.safeConditionColor : colors.dangerConditionColor, 
                data["distance"] > data["safeDistance"] ? colors.safeConditionColorOverlay : colors.dangerConditionColorOverlay)

            if(data["alert"] == "off" || data["alert"] == 0){
                document.getElementById("alertStatus").innerHTML = "desligado"
                snd.pause()
            }else{
                document.getElementById("alertStatus").innerHTML = "ligado"
                snd.play()
            }
            
            document.getElementById("safeDistance").innerHTML = data["safeDistance"] + " cm"
            document.getElementById("distance").innerHTML = Number((data["distance"]).toFixed(1)) + " cm"
            
            setTimeout(getdevice, 1000);
            if(counter >= 20){
                removeData(waterLevel_chart);
            }
            counter++
        });
}

var waterLevel_chart = new Chart(waterLevel, {
    //type: 'line',
    type: 'line',
    data: {
        labels: [],
        datasets: [{
            label: 'DistÃ¢ncia entre o sensor e o solo',
            data: [],
            fill:true,
            backgroundColor: 'rgba(244, 67, 54, 0.1)',
            borderColor:'rgba(7, 244, 34, 0.8)',
            borderWidth: 3
        }]
    },
    options: {
        scales: {
            yAxes: [{
                ticks: {
                    beginAtZero: true
                }
            }]
        }
    }
});



function addData(chart, label, data, backgroundColor, borderColor) {
    chart.data.labels.push(label);
    chart.data.datasets.forEach((dataset) => {
        dataset.data.push(data);
    });
    chart.data.datasets[0].backgroundColor = backgroundColor
    chart.data.datasets[0].borderColor = borderColor
    chart.update();
}

function removeData(chart) {
    chart.data.labels.shift();
    chart.data.datasets.forEach((dataset) => {
        dataset.data.shift();
    });
    chart.update();
}

var counter = 0; 

getdevice();
